// Mock form components
const ReportForm = () => {
  const [formData, setFormData] = useState({
    crimetype: "",
    description: "",
    crimeDate: "",
    crimeTime: "",
    location: {
      coordinates: [], // [longitude, latitude]
      address: ""
    },
    crimeimageURL: ""
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [selectedFile, setSelectedFile] = useState(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleLocationUpdate = (coordinates, address) => {
    setFormData({
      ...formData,
      location: {
        coordinates: [coordinates[1], coordinates[0]], // MongoDB uses [longitude, latitude]
        address: address
      }
    });
  };

  const handleFileChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      setSelectedFile(e.target.files[0]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    // Data validation
    if (!formData.crimetype || !formData.description || !formData.crimeDate || !formData.crimeTime) {
      setMessage("Please fill in all required fields");
      setLoading(false);
      return;
    }

    if (!formData.location.coordinates || formData.location.coordinates.length !== 2) {
      setMessage("Please select a valid location on the map");
      setLoading(false);
      return;
    }

    // Format the date properly - current format causing "Invalid Date" error
    // Convert from "YYYY-MM-DD" to a valid ISO date string
    const dateObj = new Date(formData.crimeDate + "T" + formData.crimeTime);
    if (isNaN(dateObj.getTime())) {
      setMessage("Please enter a valid date and time");
      setLoading(false);
      return;
    }

    // Create form data for file upload
    const submitData = new FormData();

    // Add all fields individually to avoid nesting issues
    submitData.append("crimetype", formData.crimetype);
    submitData.append("description", formData.description);
    submitData.append("crimeDate", dateObj.toISOString()); // Use ISO format for dates
    submitData.append("crimeTime", formData.crimeTime);
    submitData.append("coordinates", JSON.stringify(formData.location.coordinates));
    submitData.append("address", formData.location.address);

    // Add the file if selected
    if (selectedFile) {
      submitData.append("crimeimage", selectedFile);
    }

    console.log("Submitting data:", {
      crimetype: formData.crimetype,
      description: formData.description,
      crimeDate: dateObj.toISOString(),
      crimeTime: formData.crimeTime,
      coordinates: formData.location.coordinates,
      address: formData.location.address
    });

    try {
      const response = await fetch("http://localhost:4000/report/add-new", {
        method: "POST",
        body: submitData, // FormData sets its own Content-Type
      });

      let data;
      try {
        data = await response.json();
      } catch (error) {
        console.error("Error parsing response:", error);
        data = { message: "Could not parse server response" };
      }

      if (response.ok) {
        setMessage("Report submitted successfully!");
        // Reset form
        setFormData({
          crimetype: "",
          description: "",
          crimeDate: "",
          crimeTime: "",
          location: {
            coordinates: [],
            address: ""
          },
          crimeimageURL: ""
        });
        setSelectedFile(null);
      } else {
        setMessage(`Error: ${data.message || "Unknown server error"}`);
        console.error("Server response:", data);
      }
    } catch (error) {
      setMessage("Network error. Please check your connection and try again.");
      console.error("Submission error:", error);
    }

    setLoading(false);
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <div className="bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-xl font-bold mb-6">Report an Incident</h2>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Incident Type</label>
          <select
            name="crimetype"
            value={formData.crimetype}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            required
          >
            <option value="">Select incident type</option>
            <option value="Theft">Theft</option>
            <option value="Assault">Assault</option>
            <option value="Vandalism">Vandalism</option>
            <option value="Suspicious Activity">Suspicious Activity</option>
            <option value="Other">Other</option>
          </select>
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Location</label>
          <div className="border rounded p-4">
            <CurrentLocationMap
              formData={formData}
              onLocationSelect={handleLocationUpdate}
            />
            <p className="mt-2 text-sm text-gray-600">
              Drag the marker to pinpoint the exact location of the incident
            </p>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
          <div>
            <label className="block text-gray-700 mb-2">Date</label>
            <input
              type="date"
              name="crimeDate"
              value={formData.crimeDate}
              onChange={handleChange}
              className="w-full p-2 border rounded"
              required
            />
          </div>

          <div>
            <label className="block text-gray-700 mb-2">Time</label>
            <input
              type="time"
              name="crimeTime"
              value={formData.crimeTime}
              onChange={handleChange}
              className="w-full p-2 border rounded"
              required
            />
          </div>
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Description</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            className="w-full p-2 border rounded h-32"
            placeholder="Provide details about the incident..."
            required
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Upload Evidence (optional)</label>
          <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center">
            <p className="text-gray-500 mb-2">
              {selectedFile ? selectedFile.name : "Drag & drop files here or click to browse"}
            </p>
            <input
              type="file"
              id="fileInput"
              onChange={handleFileChange}
              className="hidden"
              accept="image/*"
            />
            <div
              type="button"
              variant="outline"
              size="sm"
              onClick={() => document.getElementById('fileInput').click()}
            >
              Choose Files
            </div>
          </div>
        </div>

        {message && (
          <div className={`p-3 rounded mb-4 ${message.includes('Error') ? 'bg-red-100 text-red-800' : 'bg-green-100 text-green-800'}`}>
            {message}
          </div>
        )}

        <Button
          type="submit"
          className="w-full"
          disabled={loading}
        >
          {loading ? "Submitting..." : "Submit Report"}
        </Button>
      </div>
    </form>
  );
};

