                          onClick={isRecording ? stopRecording : startRecording}
                        >
                          {isRecording ? "Stop Recording" : "Start Recording"}
                        </button>

                        {audioUrl && (
                          <div className="mt-4">
                            <audio controls>
                              <source src={audioUrl} type="audio/mpeg" />
                              Your browser does not support the audio tag.
