export default function ReportPage() {
  const [isRecording, setIsRecording] = useState(false);
  const [audioBlob, setAudioBlob] = useState(null);
  const [audioURL, setAudioURL] = useState('');
  const [loading, setLoading] = useState(false);
  const [uploadedAudio, setUploadedAudio] = useState(null);
  const [error, setError] = useState('');

  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);

  // Start recording audio
  const startRecording = async () => {
    try {
      // Reset previous recordings
      audioChunksRef.current = [];
      setAudioBlob(null);
      setAudioURL('');
      setError('');

      // Request microphone access
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });

      // Create new MediaRecorder instance with specific MIME type
      const mediaRecorder = new MediaRecorder(stream, {
        mimeType: 'audio/webm;codecs=opus' // Specify codec for better compatibility
      });
      mediaRecorderRef.current = mediaRecorder;

      // Collect audio chunks
      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          audioChunksRef.current.push(event.data);
        }
      };

      // Handle recording completion
      mediaRecorder.onstop = () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/webm' });
        const audioUrl = URL.createObjectURL(audioBlob);

        setAudioBlob(audioBlob);
        setAudioURL(audioUrl);
      };

      // Start recording with a timeslice to ensure data is collected periodically
      mediaRecorder.start(1000); // Collect data every second
      setIsRecording(true);

    } catch (err) {
      console.error('Error accessing microphone:', err);
      setError('Could not access microphone. Please ensure you have granted permission.');
    }
  };

  // Stop recording audio
  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);

      // Stop all audio tracks
      mediaRecorderRef.current.stream.getTracks().forEach(track => track.stop());
    }
  };

  // Upload recorded audio to Cloudinary
  const uploadRecording = async () => {
    if (!audioBlob) {
      setError('Please record audio first');
      return;
    }

    setLoading(true);

    try {
      // Create a proper filename with extension
      const fileName = `recording-${Date.now()}.webm`;

      // Create a File object instead of using the Blob directly
      const audioFile = new File([audioBlob], fileName, {
        type: 'audio/webm',
        lastModified: Date.now()
      });

      // Prepare the form data with the file
      const formData = new FormData();
      formData.append('audio', audioFile);

      // Ensure the form data is properly assembled
      // (You can log this for debugging, but it won't show the full content)
      console.log('FormData created:', formData);

      // Send to the server with proper timeout and size settings
      const response = await axios.post('http://localhost:4000/api/upload-audio',
        formData,
        {
          headers: {
            'Content-Type': 'multipart/form-data'
          },
          timeout: 30000, // Increase timeout for larger files
          maxContentLength: 10 * 1024 * 1024 // 10MB max size
        }
      );

      setUploadedAudio(response.data.audioData);
      setLoading(false);

    } catch (err) {
      console.error('Upload failed:', err);
      setError('Failed to upload audio. ' + (err.response?.data?.message || err.message));
      setLoading(false);
    }
  };
  const [chatMsg, setChatMsg] = useState('')
  const getChat = async () => {

    const formData = {
      "crime_type": chatMsg
    }
    const response = await axios.post('http://127.0.0.1:5000/crime-guidance',
      formData,
      {
        headers: {
          'Content-Type': 'multipart/form-data'
        },
      }
    );

    console.log(response)
  }

  return (
    <div className="min-h-screen bg-gray-50 text-gray-900">
      <div className="container mx-auto px-4 py-8">
        <a href="/" className="flex items-center gap-2 mb-6 text-gray-500 hover:text-gray-900">
          <ArrowLeft className="h-4 w-4" />
          <span>Back to Home</span>
        </a>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          <div className="md:col-span-2">
            <Card>
              <CardHeader>
                <CardTitle>Report an Incident</CardTitle>
                <CardDescription>
                  Fill out the form below to report a crime or suspicious activity. All reports are reviewed by law
                  enforcement before being published.
                </CardDescription>
              </CardHeader>
              <CardContent>
                <Tabs defaultValue="form">
                  <TabsList className="grid grid-cols-2 mb-6 w-full">
                    <TabsTrigger value="form">Form</TabsTrigger>
                    <TabsTrigger value="voice">Voice Report</TabsTrigger>
                  </TabsList>
                  <TabsContent value="form">
                    {/* form */}
                    <ReportForm />
                  </TabsContent>
                  <TabsContent value="voice">
                    <div className="flex flex-col items-center justify-center py-12 space-y-4">
                      <div className="w-16 h-16 rounded-full bg-gray-300 flex items-center justify-center">
                        <svg
                          xmlns="http://www.w3.org/2000/svg"
                          viewBox="0 0 24 24"
                          fill="none"
                          stroke="currentColor"
                          strokeWidth="2"
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          className="h-8 w-8 text-black-600"
                        >
                          <path d="M12 2a3 3 0 0 0-3 3v7a3 3 0 0 0 6 0V5a3 3 0 0 0-3-3Z" />
                          <path d="M19 10v2a7 7 0 0 1-14 0v-2" />
                          <line x1="12" x2="12" y1="19" y2="22" />
                        </svg>
                      </div>
                      <h3 className="text-xl font-medium text-gray-900">Voice Reporting</h3>
                      <p className="text-center text-gray-600 max-w-md">
                        Click the button below to start recording your report. Speak clearly and provide as much detail as
                        possible.
                      </p>

                      {/* <div>
                        <h2>Upload an Audio File</h2>
                        <form onSubmit={handleSubmit}>
                          <input type="file" accept="audio/*" onChange={handleFileChange} />
                          <button type="submit">Upload</button>
                        </form>
                        <p>{message}</p>
                      </div> */}
                      {/* <Button size="lg" className="mt-4">
                        Start Recording
                      </Button> */}


                      <div className="audio-recorder-container">
                        <h2>Record and Upload Audio to Cloudinary</h2>

                        <div className="recorder-controls">
                          {!isRecording ? (
                            <button
                              onClick={startRecording}
                              className="start-btn"
                              disabled={isRecording}
                            >
                              Start Recording
                            </button>
                          ) : (
                            <button
                              onClick={stopRecording}
                              className="stop-btn"
                              disabled={!isRecording}
                            >
                              Stop Recording
                            </button>
                          )}
                        </div>

                        {audioURL && (
                          <div className="recorded-audio">
                            <h3>Recording Preview:</h3>
                            <audio controls src={audioURL}>
                              Your browser does not support the audio element.
                            </audio>

                            <button
                              onClick={uploadRecording}
                              disabled={loading || !audioBlob}
                              className="upload-btn"
                            >
                              {loading ? 'Uploading...' : 'Upload to Cloudinary'}
                            </button>
                          </div>
                        )}

                        {error && <p className="error-message">{error}</p>}

                        {loading && <p>Uploading your audio recording...</p>}

                        {uploadedAudio && (
                          <div className="uploaded-audio">
                            <h3>Upload Successful!</h3>
                            <p>Audio URL: <a href={uploadedAudio.path} target="_blank" rel="noopener noreferrer">{uploadedAudio.path}</a></p>

                            <div className="audio-player">
                              <h4>Uploaded Audio:</h4>
                              <audio controls src={uploadedAudio.path}>
                                Your browser does not support the audio element.
                              </audio>
                            </div>

                            <div className="audio-details">
                              <h4>File Details:</h4>
                              <p>Format: {uploadedAudio.format}</p>
                              <p>Size: {Math.round(uploadedAudio.size / 1024)} KB</p>
                            </div>
                          </div>
                        )}
                      </div>
                    </div>
                  </TabsContent>
                </Tabs>
              </CardContent>
            </Card>
          </div>
          {/* Ai Chat boat */}
          <div>
            <Card className="sticky top-8">
              <CardHeader>
                <CardTitle>Need Help?</CardTitle>
                <CardDescription>
                  Our AI assistant can guide you through the reporting process and answer questions about crime reporting.
                </CardDescription>
              </CardHeader>
              <CardContent>
                <ChatbotAssistant setChatMsg={setChatMsg} chatMsg={chatMsg} handleSubmit={getChat}/>
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    </div>
  );
}
