'use client'
import React, { useState, useEffect } from "react";
import { format } from "date-fns";

const ReportStatusPage = () => {
    const [reports, setReports] = useState([]);
    const [loading, setLoading] = useState(true);
    const [searchQuery, setSearchQuery] = useState("");
    const [statusFilter, setStatusFilter] = useState("all");
    const [selectedReport, setSelectedReport] = useState(null);
    const [detailsOpen, setDetailsOpen] = useState(false);
    const [activeTab, setActiveTab] = useState("list");
    const [detailTab, setDetailTab] = useState("details");

    useEffect(() => {
        // Fetch reports from API
        // Replace with your actual API endpoint
        const fetchReports = async () => {
            try {
                // Mock data for demonstration
                // You'll replace this with your actual API call
                const mockReports = [
                    {
                        reportId: "REP-2025-0412",
                        fullName: "John Doe",
                        email: "john@example.com",
                        crimetype: "Suspicious Activity",
                        location: {
                            coordinates: [-73.9654, 40.7829],
                            address: "Central Park, East Entrance"
                        },
                        crimeDate: "2025-04-01T00:00:00.000Z",
                        crimeTime: "14:30",
                        description: "I noticed a group of individuals behaving suspiciously near the east entrance of Central Park.",
                        crimeimageURLs: ["/placeholder1.jpg", "/placeholder2.jpg"],
                        reportedAt: "2025-04-01T14:30:00.000Z",
                        status: "under_review",
                        lastUpdated: "2025-04-01T18:45:00.000Z",
                        timeline: [
                            { date: "2025-04-01T14:30:00.000Z", status: "submitted", note: "Report submitted" },
                            { date: "2025-04-01T14:35:00.000Z", status: "received", note: "Report received by system" },
                            { date: "2025-04-01T18:45:00.000Z", status: "under_review", note: "Under review by Officer Johnson" },
                        ],
                        assignedTo: "Officer Johnson"
                    },
                    {
                        reportId: "REP-2025-0389",
                        fullName: "Jane Smith",
                        email: "jane@example.com",
                        crimetype: "Theft",
                        location: {
                            coordinates: [-73.9876, 40.7589],
                            address: "Downtown Mall, Parking Level B"
                        },
                        crimeDate: "2025-03-28T00:00:00.000Z",
                        crimeTime: "09:15",
                        description: "My car was broken into while parked at Downtown Mall. The passenger window was smashed.",
                        crimeimageURLs: ["/placeholder1.jpg", "/placeholder2.jpg", "/placeholder3.jpg"],
                        reportedAt: "2025-03-28T09:15:00.000Z",
                        status: "approved",
                        lastUpdated: "2025-04-01T11:20:00.000Z",
                        timeline: [
                            { date: "2025-03-28T09:15:00.000Z", status: "submitted", note: "Report submitted" },
                            { date: "2025-03-28T09:20:00.000Z", status: "received", note: "Report received by system" },
                            { date: "2025-03-28T14:30:00.000Z", status: "under_review", note: "Under review by Officer Martinez" },
                            { date: "2025-04-01T11:20:00.000Z", status: "approved", note: "Report approved and case number assigned: PD-2025-1142" },
                        ],
                        assignedTo: "Officer Martinez"
                    },
                    {
                        reportId: "REP-2025-0356",
                        fullName: "Robert Johnson",
                        email: "robert@example.com",
                        crimetype: "Vandalism",
                        location: {
                            coordinates: [-73.9543, 40.8136],
                            address: "Riverside Community Center"
                        },
                        crimeDate: "2025-03-25T00:00:00.000Z",
                        crimeTime: "16:45",
                        description: "The exterior wall of the community center was vandalized with graffiti overnight.",
                        crimeimageURLs: ["/placeholder1.jpg", "/placeholder2.jpg", "/placeholder3.jpg"],
                        reportedAt: "2025-03-25T16:45:00.000Z",
                        status: "resolved",
                        lastUpdated: "2025-04-01T09:30:00.000Z",
                        timeline: [
                            { date: "2025-03-25T16:45:00.000Z", status: "submitted", note: "Report submitted" },
                            { date: "2025-03-25T16:50:00.000Z", status: "received", note: "Report received by system" },
                            { date: "2025-03-25T18:15:00.000Z", status: "under_review", note: "Under review by Officer Williams" },
                            { date: "2025-03-26T09:30:00.000Z", status: "approved", note: "Report approved and case number assigned: PD-2025-1098" },
                            { date: "2025-04-01T09:30:00.000Z", status: "resolved", note: "Case resolved - Suspects identified" },
                        ],
                        assignedTo: "Officer Williams"
                    }
                ];

                setReports(mockReports);
                setLoading(false);
            } catch (error) {
                console.error("Error fetching reports:", error);
                setLoading(false);
            }
        };

        fetchReports();
    }, []);

    // Format date helper function
    const formatDate = (dateString) => {
        const date = new Date(dateString);
        return format(date, "MMM d, yyyy h:mm a");
    };

    // Filter reports based on search query and status filter
    const filteredReports = reports.filter((report) => {
        const matchesSearch =
            report.reportId.toLowerCase().includes(searchQuery.toLowerCase()) ||
            report.crimetype.toLowerCase().includes(searchQuery.toLowerCase()) ||
            report.location.address.toLowerCase().includes(searchQuery.toLowerCase());

        const matchesStatus = statusFilter === "all" || report.status === statusFilter;

        return matchesSearch && matchesStatus;
    });

    // Status badge mapping
    const getStatusBadge = (status) => {
        const statusMap = {
            submitted: {
                label: "Submitted",
                bgColor: "bg-gray-100",
                textColor: "text-gray-700",
                icon: (
                    <svg className="h-3 w-3 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                    </svg>
                ),
            },
            received: {
                label: "Received",
                bgColor: "bg-blue-100",
                textColor: "text-blue-700",
                icon: (
                    <svg className="h-3 w-3 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                    </svg>
                ),
            },
            under_review: {
                label: "Under Review",
                bgColor: "bg-yellow-100",
                textColor: "text-yellow-700",
                icon: (
                    <svg className="h-3 w-3 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                    </svg>
                ),
            },
            approved: {
                label: "Approved",
                bgColor: "bg-green-100",
                textColor: "text-green-700",
                icon: (
                    <svg className="h-3 w-3 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                    </svg>
                ),
            },
            investigating: {
                label: "Investigating",
                bgColor: "bg-indigo-100",
                textColor: "text-indigo-700",
                icon: (
                    <svg className="h-3 w-3 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                    </svg>
                ),
            },
            resolved: {
                label: "Resolved",
                bgColor: "bg-green-100",
                textColor: "text-green-700",
                icon: (
                    <svg className="h-3 w-3 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                    </svg>
                ),
            },
            closed: {
                label: "Closed",
                bgColor: "bg-purple-100",
                textColor: "text-purple-700",
                icon: (
                    <svg className="h-3 w-3 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                    </svg>
                ),
            },
            rejected: {
                label: "Rejected",
                bgColor: "bg-red-100",
                textColor: "text-red-700",
                icon: (
                    <svg className="h-3 w-3 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z" />
                    </svg>
                ),
            },
        };

        const defaultStatus = {
            label: "Unknown",
            bgColor: "bg-gray-100",
            textColor: "text-gray-700",
            icon: (
                <svg className="h-3 w-3 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z" />
                </svg>
            ),
        };

        return statusMap[status] || defaultStatus;
    };

    // Progress mapping
    const getStatusProgress = (status) => {
        const progressMap = {
            submitted: 10,
            received: 20,
            under_review: 40,
            approved: 60,
            investigating: 70,
            resolved: 90,
            closed: 100,
            rejected: 100,
        };

        return progressMap[status] || 0;
    };

    return (
        <div className="min-h-screen bg-gray-50">
            <div className="container mx-auto px-4 py-8 max-w-7xl">
                <button
                    className="mb-6 flex items-center gap-2 text-gray-500 hover:text-gray-700"
                >
                    <svg className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 19l-7-7m0 0l7-7m-7 7h18" />
                    </svg>
                    <span>Back to Dashboard</span>
                </button>

                <div className="grid grid-cols-1 gap-8">
                    <div className="bg-white rounded-lg shadow-lg border border-gray-100">
                        <div className="bg-blue-50 border-b border-gray-200 px-6 py-4">
                            <div className="flex items-start gap-4">
                                <div className="rounded-full bg-blue-100 p-2">
                                    <svg className="h-6 w-6 text-blue-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                                    </svg>
                                </div>
                                <div>
                                    <h1 className="text-2xl font-bold">Report Status</h1>
                                    <p className="mt-2 text-base text-gray-600">
                                        Track the status of your submitted crime reports and view updates from law enforcement.
                                    </p>
                                </div>
                            </div>
                        </div>
                        <div className="p-6">
                            <div className="flex flex-col md:flex-row gap-4 mb-6">
                                <div className="relative flex-1">
                                    <svg className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                                    </svg>
                                    <input
                                        type="text"
                                        placeholder="Search by report ID, crime type, or location"
                                        className="pl-10 w-full border border-gray-300 rounded-md px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                        value={searchQuery}
                                        onChange={(e) => setSearchQuery(e.target.value)}
                                    />
                                </div>
                                <div className="w-full md:w-64">
                                    <div className="relative">
                                        <select
                                            value={statusFilter}
                                            onChange={(e) => setStatusFilter(e.target.value)}
                                            className="w-full bg-white border border-gray-300 rounded-md px-4 py-2 appearance-none focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                        >
                                            <option value="all">All Statuses</option>
                                            <option value="submitted">Submitted</option>
                                            <option value="received">Received</option>
                                            <option value="under_review">Under Review</option>
                                            <option value="approved">Approved</option>
                                            <option value="investigating">Investigating</option>
                                            <option value="resolved">Resolved</option>
                                            <option value="closed">Closed</option>
                                            <option value="rejected">Rejected</option>
                                        </select>
                                        <div className="absolute inset-y-0 right-0 flex items-center pr-3 pointer-events-none">
                                            <svg className="h-4 w-4 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                                            </svg>
                                        </div>
                                    </div>
                                </div>
                            </div>

                            <div className="mb-6">
                                <div className="flex border-b">
                                    <button
                                        className={`px-4 py-2 font-medium ${activeTab === 'list' ? 'text-blue-600 border-b-2 border-blue-600' : 'text-gray-500'}`}
                                        onClick={() => setActiveTab('list')}
                                    >
                                        List View
                                    </button>
                                    <button
                                        className={`px-4 py-2 font-medium ${activeTab === 'timeline' ? 'text-blue-600 border-b-2 border-blue-600' : 'text-gray-500'}`}
                                        onClick={() => setActiveTab('timeline')}
                                    >
                                        Timeline View
                                    </button>
                                </div>
                            </div>

                            {loading ? (
                                <div className="flex justify-center items-center h-64">
                                    <div className="animate-spin rounded-full h-10 w-10 border-t-2 border-b-2 border-blue-500"></div>
                                </div>
                            ) : (
                                <>
                                    {activeTab === 'list' && (
                                        <div className="space-y-4">
                                            {filteredReports.length > 0 ? (
                                                filteredReports.map((report) => {
                                                    const statusInfo = getStatusBadge(report.status);
                                                    const progressValue = getStatusProgress(report.status);

                                                    return (
                                                        <div key={report.reportId} className="bg-white border border-gray-200 rounded-lg overflow-hidden hover:shadow-md transition-shadow">
                                                            <div className="flex flex-col md:flex-row">
                                                                <div className="flex-1 p-4">
                                                                    <div className="flex flex-col md:flex-row md:items-center justify-between mb-2">
                                                                        <div className="flex items-center gap-2 mb-2 md:mb-0">
                                                                            <h3 className="font-semibold">{report.reportId}</h3>
                                                                            <span className={`flex items-center px-2 py-1 rounded-full text-xs font-medium ${statusInfo.bgColor} ${statusInfo.textColor}`}>
                                                                                {statusInfo.icon}
                                                                                {statusInfo.label}
                                                                            </span>
                                                                        </div>
                                                                        <div className="text-sm text-gray-500">
                                                                            Reported: {formatDate(report.reportedAt)}
                                                                        </div>
                                                                    </div>

                                                                    <h2 className="text-lg font-medium mb-2">{report.crimetype}</h2>

                                                                    <div className="grid grid-cols-1 md:grid-cols-2 gap-2 mb-3">
                                                                        <div className="flex items-center gap-2 text-sm text-gray-500">
                                                                            <svg className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                                                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z" />
                                                                            </svg>
                                                                            <span>{formatDate(report.crimeDate).split(",")[0]} at {report.crimeTime}</span>
                                                                        </div>
                                                                        <div className="flex items-center gap-2 text-sm text-gray-500">
                                                                            <svg className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                                                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                                                                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                                                                            </svg>
                                                                            <span>{report.location.address}</span>
                                                                        </div>
                                                                    </div>

                                                                    <div className="mb-3">
                                                                        <div className="flex justify-between text-xs text-gray-500 mb-1">
                                                                            <span>Progress</span>
                                                                            <span>{progressValue}%</span>
                                                                        </div>
                                                                        <div className="h-2 bg-gray-200 rounded-full overflow-hidden">
                                                                            <div
                                                                                className="h-full bg-blue-500 rounded-full"
                                                                                style={{ width: `${progressValue}%` }}
                                                                            ></div>
                                                                        </div>
                                                                    </div>

                                                                    <div className="flex items-center justify-between">
                                                                        <div className="flex items-center gap-2">
                                                                            {report.crimeimageURLs.length > 0 && (
                                                                                <span className="px-2 py-1 bg-blue-50 text-blue-700 border border-blue-200 rounded-md text-xs">
                                                                                    {report.crimeimageURLs.length} Media Files
                                                                                </span>
                                                                            )}
                                                                            {report.timeline.some(item => item.note.includes("case number assigned")) && (
                                                                                <span className="px-2 py-1 bg-purple-50 text-purple-700 border border-purple-200 rounded-md text-xs">
                                                                                    Case #{report.timeline.find(item => item.note.includes("case number assigned")).note.split(": ")[1]}
                                                                                </span>
                                                                            )}
                                                                        </div>

                                                                        <button
                                                                            className="px-3 py-1 border border-gray-300 rounded-md text-sm flex items-center hover:bg-gray-50"
                                                                            onClick={() => {
                                                                                setSelectedReport(report);
                                                                                setDetailsOpen(true);
                                                                            }}
                                                                        >
                                                                            <svg className="h-3 w-3 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                                                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                                                                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
                                                                            </svg>
                                                                            View Details
                                                                        </button>
                                                                    </div>
                                                                </div>

                                                                {report.status === "approved" && (
                                                                    <div className="w-full md:w-32 bg-green-50 flex items-center justify-center p-4 border-t md:border-t-0 md:border-l border-green-100">
                                                                        <div className="text-center">
                                                                            <div className="rounded-full bg-green-100 p-2 mx-auto mb-2">
                                                                                <svg className="h-5 w-5 text-green-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                                                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                                                                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                                                                                </svg>
                                                                            </div>
                                                                            <p className="text-xs font-medium text-green-800">Visible on Map</p>
                                                                        </div>
                                                                    </div>
                                                                )}
                                                            </div>
                                                        </div>
                                                    );
                                                })
                                            ) : (
                                                <div className="text-center py-12">
                                                    <div className="rounded-full bg-gray-100 p-3 mx-auto mb-4 w-fit">
                                                        <svg className="h-6 w-6 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                                                        </svg>
                                                    </div>
                                                    <h3 className="text-lg font-medium mb-2">No reports found</h3>
                                                    <p className="text-gray-500">Try adjusting your search or filter criteria</p>
                                                </div>
                                            )}
                                        </div>
                                    )}

                                    {activeTab === 'timeline' && (
                                        <div className="space-y-6">
                                            {filteredReports.length > 0 ? (
                                                filteredReports.map((report) => {
                                                    const statusInfo = getStatusBadge(report.status);

                                                    return (
                                                        <div key={report.reportId} className="bg-white border border-gray-200 rounded-lg p-4">
                                                            <div className="flex flex-col md:flex-row md:items-center justify-between mb-4">
                                                                <div>
                                                                    <h3 className="font-semibold text-lg">{report.crimetype}</h3>
                                                                    <div className="flex items-center gap-2 text-sm text-gray-500">
                                                                        <span>{report.reportId}</span>
                                                                        <span></span>
                                                                        <span>{report.location.address}</span>
                                                                    </div>
                                                                </div>
                                                                <span className={`flex items-center px-2 py-1 rounded-full text-xs font-medium mt-2 md:mt-0 w-fit ${statusInfo.bgColor} ${statusInfo.textColor}`}>
                                                                    {statusInfo.icon}
                                                                    {statusInfo.label}
                                                                </span>
                                                            </div>

                                                            <div className="space-y-3 pl-4 border-l-2 border-gray-200">
                                                                {report.timeline.map((event, index) => {
                                                                    const eventStatus = getStatusBadge(event.status);

                                                                    return (
                                                                        <div key={index} className="relative">
                                                                            <div className="absolute -left-[17px] w-4 h-4 rounded-full bg-white border-2 border-blue-500"></div>
                                                                            <div className="flex flex-col md:flex-row md:items-center justify-between">
                                                                                <div>
                                                                                    <p className="text-sm">{event.note}</p>
                                                                                </div>
                                                                                <div className="text-xs text-gray-500 mt-1 md:mt-0">
                                                                                    {formatDate(event.date)}
                                                                                </div>
                                                                            </div>
                                                                        </div>
                                                                    );
                                                                })}
                                                            </div>

                                                            <div className="flex justify-end mt-4">
                                                                <button
                                                                    className="px-3 py-2 border border-gray-300 rounded-md text-sm flex items-center hover:bg-gray-50"
                                                                    onClick={() => {
                                                                        setSelectedReport(report);
                                                                        setDetailsOpen(true);
                                                                    }}
                                                                >
                                                                    <svg className="h-4 w-4 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                                                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
                                                                    </svg>
                                                                    View Full Report
                                                                </button>
                                                            </div>
                                                        </div>
                                                    );
                                                })
                                            ) : (
                                                <div className="text-center py-12">
                                                    <div className="rounded-full bg-gray-100 p-3 mx-auto mb-4 w-fit">
                                                        <svg className="h-6 w-6 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                                                        </svg>
                                                    </div>

                                                    {/* // ... (previous code remains unchanged until the timeline view empty state) */}

                                                    <h3 className="text-lg font-medium mb-2">No reports found</h3>
                                                    <p className="text-gray-500">Try adjusting your search or filter criteria</p>
                                                </div>
                                                // </div>
                                            )}
                                        </div>
                                    )}
                                </>
                            )}
                        </div>
                    </div>
                </div>

                {/* Report Details Modal */}
                {detailsOpen && selectedReport && (
                    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
                        <div className="bg-white rounded-lg shadow-xl max-w-2xl w-full mx-4 max-h-[90vh] overflow-y-auto">
                            <div className="p-6">
                                <div className="flex justify-between items-center mb-4">
                                    <h2 className="text-xl font-bold">Report {selectedReport.reportId}</h2>
                                    <button
                                        onClick={() => setDetailsOpen(false)}
                                        className="text-gray-500 hover:text-gray-700"
                                    >
                                        <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                                        </svg>
                                    </button>
                                </div>

                                <div className="mb-6">
                                    <div className="flex border-b">
                                        <button
                                            className={`px-4 py-2 font-medium ${detailTab === 'details' ? 'text-blue-600 border-b-2 border-blue-600' : 'text-gray-500'}`}
                                            onClick={() => setDetailTab('details')}
                                        >
                                            Details
                                        </button>
                                        <button
                                            className={`px-4 py-2 font-medium ${detailTab === 'timeline' ? 'text-blue-600 border-b-2 border-blue-600' : 'text-gray-500'}`}
                                            onClick={() => setDetailTab('timeline')}
                                        >
                                            Timeline
                                        </button>
                                        <button
                                            className={`px-4 py-2 font-medium ${detailTab === 'media' ? 'text-blue-600 border-b-2 border-blue-600' : 'text-gray-500'}`}
                                            onClick={() => setDetailTab('media')}
                                        >
                                            Media
                                        </button>
                                    </div>
                                </div>

                                {detailTab === 'details' && (
                                    <div className="space-y-4">
                                        <div>
                                            <h3 className="font-semibold">Crime Type</h3>
                                            <p>{selectedReport.crimetype}</p>
                                        </div>
                                        <div>
                                            <h3 className="font-semibold">Location</h3>
                                            <p>{selectedReport.location.address}</p>
                                            <p>Coordinates: {selectedReport.location.coordinates.join(', ')}</p>
                                        </div>
                                        <div>
                                            <h3 className="font-semibold">Date & Time</h3>
                                            <p>{formatDate(selectedReport.crimeDate)} at {selectedReport.crimeTime}</p>
                                        </div>
                                        <div>
                                            <h3 className="font-semibold">Description</h3>
                                            <p>{selectedReport.description}</p>
                                        </div>
                                        <div>
                                            <h3 className="font-semibold">Status</h3>
                                            <span className={`inline-flex items-center px-2 py-1 rounded-full text-sm font-medium ${getStatusBadge(selectedReport.status).bgColor} ${getStatusBadge(selectedReport.status).textColor}`}>
                                                {getStatusBadge(selectedReport.status).icon}
                                                {getStatusBadge(selectedReport.status).label}
                                            </span>
                                        </div>
                                        <div>
                                            <h3 className="font-semibold">Assigned To</h3>
                                            <p>{selectedReport.assignedTo}</p>
                                        </div>
                                    </div>
                                )}

                                {detailTab === 'timeline' && (
                                    <div className="space-y-4">
                                        {selectedReport.timeline.map((event, index) => (
                                            <div key={index} className="flex items-start gap-3">
                                                <div className="flex-shrink-0 w-2 h-2 mt-1.5 rounded-full bg-blue-500"></div>
                                                <div>
                                                    <p className="font-medium">{event.note}</p>
                                                    <p className="text-sm text-gray-500">{formatDate(event.date)}</p>
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                )}

                                {detailTab === 'media' && (
                                    <div className="space-y-4">
                                        {selectedReport.crimeimageURLs.length > 0 ? (
                                            <div className="grid grid-cols-2 gap-4">
                                                {selectedReport.crimeimageURLs.map((url, index) => (
                                                    <img
                                                        key={index}
                                                        src={url}
                                                        alt={`Crime evidence ${index + 1}`}
                                                        className="w-full h-40 object-cover rounded-md"
                                                    />
                                                ))}
                                            </div>
                                        ) : (
                                            <p className="text-gray-500">No media files attached to this report.</p>
                                        )}
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </div >
    );
};

export default ReportStatusPage;
