import React, { useState, useRef } from 'react';
import { ArrowLeft } from 'lucide-react';
import CurrentLocationMap from '../../components/Map/Map'
import axios from 'axios';

// Mock components
const Button = ({ size, className, children, fun = () => { } }) => {
  const sizeClasses = {
    lg: 'py-3 px-6 text-lg'
  };

  return (
    <button onClick={fun}
      className={`bg-black text-white rounded transition-colors hover:bg-gray-800 ${sizeClasses[size] || 'py-2 px-4'} ${className || ''}`}
    >
      {children}
    </button>
  );
};

const Card = ({ className, children }) => (
  <div className={`bg-white rounded-lg overflow-hidden shadow-md ${className || ''}`}>
    {children}
  </div>
);

const CardHeader = ({ children }) => (
  <div className="p-6 border-b border-gray-200">
    {children}
  </div>
);

const CardTitle = ({ children }) => (
  <h3 className="text-xl font-bold text-gray-900 mb-2">
    {children}
  </h3>
);

const CardDescription = ({ children }) => (
  <p className="text-gray-600">
    {children}
  </p>
);

const CardContent = ({ children }) => (
  <div className="p-6">
    {children}
  </div>
);

const Tabs = ({ defaultValue, children }) => {
  const [activeTab, setActiveTab] = React.useState(defaultValue);

  // Clone children and pass activeTab state
  const childrenWithProps = React.Children.map(children, child => {
    if (React.isValidElement(child)) {
      return React.cloneElement(child, { activeTab, setActiveTab });
    }
    return child;
  });

  return <div>{childrenWithProps}</div>;
};

const TabsList = ({ className, children, activeTab, setActiveTab }) => {
  // Clone TabsTrigger children and pass state
  const childrenWithProps = React.Children.map(children, child => {
    if (React.isValidElement(child)) {
      return React.cloneElement(child, { activeTab, setActiveTab });
    }
    return child;
  });

  return (
    <div className={`flex rounded-md bg-gray-100 p-1 ${className || ''}`}>
      {childrenWithProps}
    </div>
  );
};

const TabsTrigger = ({ value, children, activeTab, setActiveTab }) => (
  <button
    className={`flex-1 text-center py-2 px-4 rounded ${activeTab === value
      ? 'bg-white text-black-600 shadow-sm'
      : 'text-gray-600 hover:text-gray-900'
      }`}
    onClick={() => setActiveTab(value)}
  >
    {children}
  </button>
);

const TabsContent = ({ value, children, activeTab }) => {
  if (value !== activeTab) return null;
  return <div>{children}</div>;
};

// import React, { useState } from 'react';
// import axios from 'axios';

// const UploadAudio = () => {

//   return (
//     <div>
//       <h2>Upload an Audio File</h2>
//       <form onSubmit={handleSubmit}>
//         <input type="file" accept="audio/*" onChange={handleFileChange} />
//         <button type="submit">Upload</button>
//       </form>
//       <p>{message}</p>
//     </div>
//   );
// };

// export default UploadAudio;

// Mock form components
const ReportForm = () => {
  const [formData, setFormData] = useState({
    crimetype: "",
    description: "",
    crimeDate: "",
    crimeTime: "",
    location: {
      coordinates: [], // [longitude, latitude]
      address: ""
    },
    crimeimageURL: ""
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [selectedFile, setSelectedFile] = useState(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleLocationUpdate = (coordinates, address) => {
    setFormData({
      ...formData,
      location: {
        coordinates: [coordinates[1], coordinates[0]], // MongoDB uses [longitude, latitude]
        address: address
      }
    });
  };

  const handleFileChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      setSelectedFile(e.target.files[0]);
    }
  };

// Add this function to your React component
const handleSubmit = async (e) => {
  e.preventDefault();
  setLoading(true);
  setMessage('');

  try {
    // Format the data for the API
    const incidentData = {
      crimetype: formData.crimetype,
      location: {
        latitude: formData.location.lat,
        longitude: formData.location.lng
      },
      crimeDate: formData.crimeDate,
      crimeTime: formData.crimeTime,
      description: formData.description
    };

    // Send the data to your API
    const response = await fetch('http://localhost:5000/report/incidents', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(incidentData)
    });

    const result = await response.json();

    if (response.ok) {
      setMessage('Incident report submitted successfully!');
      // Reset form data if needed
      setFormData({
        crimetype: '',
        location: { lat: null, lng: null },
        crimeDate: '',
        crimeTime: '',
        description: ''
      });
      setSelectedFile(null);
    } else {
      setMessage(`Error: ${result.message || 'Failed to submit report'}`);
    }
  } catch (error) {
    console.error('Error submitting form:', error);
    setMessage('Error: Network or server issue. Please try again.');
  } finally {
    setLoading(false);
  }
};

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <div className="bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-xl font-bold mb-6">Report an Incident</h2>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Incident Type</label>
          <select
            name="crimetype"
            value={formData.crimetype}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            required
          >
            <option value="">Select incident type</option>
            <option value="Theft">Theft</option>
            <option value="Assault">Assault</option>
            <option value="Vandalism">Vandalism</option>
            <option value="Suspicious Activity">Suspicious Activity</option>
            <option value="Other">Other</option>
          </select>
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Location</label>
          <div className="border rounded p-4">
            <CurrentLocationMap
              formData={formData}
              onLocationSelect={handleLocationUpdate}
            />
            <p className="mt-2 text-sm text-gray-600">
              Drag the marker to pinpoint the exact location of the incident
            </p>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
          <div>
            <label className="block text-gray-700 mb-2">Date</label>
            <input
              type="date"
              name="crimeDate"
              value={formData.crimeDate}
              onChange={handleChange}
              className="w-full p-2 border rounded"
              required
            />
          </div>

          <div>
            <label className="block text-gray-700 mb-2">Time</label>
            <input
              type="time"
              name="crimeTime"
              value={formData.crimeTime}
              onChange={handleChange}
              className="w-full p-2 border rounded"
              required
            />
          </div>
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Description</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            className="w-full p-2 border rounded h-32"
            placeholder="Provide details about the incident..."
            required
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Upload Evidence (optional)</label>
          <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center">
            <p className="text-gray-500 mb-2">
              {selectedFile ? selectedFile.name : "Drag & drop files here or click to browse"}
            </p>
            <input
              type="file"
              id="fileInput"
              onChange={handleFileChange}
              className="hidden"
              accept="image/*"
            />
            <div
              type="button"
              variant="outline"
              size="sm"
              onClick={() => document.getElementById('fileInput').click()}
            >
              Choose Files
            </div>
          </div>
        </div>

        {message && (
          <div className={`p-3 rounded mb-4 ${message.includes('Error') ? 'bg-red-100 text-red-800' : 'bg-green-100 text-green-800'}`}>
            {message}
          </div>
        )}

        <Button
          type="submit"
          className="w-full"
          disabled={loading}
        >
          {loading ? "Submitting..." : "Submit Report"}
        </Button>
      </div>
    </form>
  );
};

// Mock chatbot assistant
const ChatbotAssistant = () => {
  const [chatMsg, setChatMsg] = useState('');
  const [messages, setMessages] = useState([]); // State to store messages

  const getChat = async (e) => {
    e.preventDefault();

    if (!chatMsg) return; // Don't send empty messages

    // Add the user's message to the messages array
    setMessages([...messages, { sender: 'user', text: chatMsg }]);

    try {
      // Send the chat message to the backend
      const response = await axios.post('http://127.0.0.1:5000/crime-guidance', { crime_type: chatMsg }, {
        headers: {
          'Content-Type': 'application/json',
        },
      });
      console.log(response)

      // Add the chatbot's response to the messages array
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: 'bot', text: response.data.message || 'I didn\'t understand that.' }
      ]);

      // Clear the input field
      setChatMsg('');

    } catch (error) {
      console.error('Error during the request:', error);
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: 'bot', text: 'An error occurred while processing your request.' }
      ]);
    }
  };

  return (
    <div className="rounded-lg bg-gray-50 p-4 h-96 flex flex-col">
      <div className="flex-1 overflow-y-auto mb-4 space-y-4">
        {messages.map((msg, index) => (
          <div
            key={index}
            className={`p-3 rounded ${msg.sender === 'user' ? 'bg-blue-500 text-white' : 'bg-gray-300 text-gray-800'}`}
          >
            <p>{msg.text}</p>
          </div>
        ))}
      </div>

      <div className="flex gap-2">
        <input
          type="text"
          className="flex-1 p-2 rounded bg-white border border-gray-300 text-gray-900"
          placeholder="Type your message..."
          value={chatMsg}
          onChange={(e) => setChatMsg(e.target.value)}
        />
        <button onClick={getChat} className="bg-blue-500 text-white p-2 rounded">
          Send
        </button>
      </div>
    </div>
  );
};

export default function ReportPage() {
  const [isRecording, setIsRecording] = useState(false);
  const [audioBlob, setAudioBlob] = useState(null);
  const [audioURL, setAudioURL] = useState('');
  const [loading, setLoading] = useState(false);
  const [uploadedAudio, setUploadedAudio] = useState(null);
  const [error, setError] = useState('');

  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);

  // Start recording audio
  const startRecording = async () => {
    try {
      // Reset previous recordings
      audioChunksRef.current = [];
      setAudioBlob(null);
      setAudioURL('');
      setError('');

      // Request microphone access
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });

      // Create new MediaRecorder instance with specific MIME type
      const mediaRecorder = new MediaRecorder(stream, {
        mimeType: 'audio/webm;codecs=opus' // Specify codec for better compatibility
      });
      mediaRecorderRef.current = mediaRecorder;

      // Collect audio chunks
      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          audioChunksRef.current.push(event.data);
        }
      };

      // Handle recording completion
      mediaRecorder.onstop = () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/webm' });
        const audioUrl = URL.createObjectURL(audioBlob);

        setAudioBlob(audioBlob);
        setAudioURL(audioUrl);
      };

      // Start recording with a timeslice to ensure data is collected periodically
      mediaRecorder.start(1000); // Collect data every second
      setIsRecording(true);

    } catch (err) {
      console.error('Error accessing microphone:', err);
      setError('Could not access microphone. Please ensure you have granted permission.');
    }
  };

  // Stop recording audio
  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);

      // Stop all audio tracks
      mediaRecorderRef.current.stream.getTracks().forEach(track => track.stop());
    }
  };

  // Upload recorded audio to Cloudinary
  const uploadRecording = async () => {
    if (!audioBlob) {
      setError('Please record audio first');
      return;
    }

    setLoading(true);

    try {
      // Create a proper filename with extension
      const fileName = `recording-${Date.now()}.webm`;

      // Create a File object instead of using the Blob directly
      const audioFile = new File([audioBlob], fileName, {
        type: 'audio/webm',
        lastModified: Date.now()
      });

      // Prepare the form data with the file
      const formData = new FormData();
      formData.append('audio', audioFile);

      // Ensure the form data is properly assembled
      // (You can log this for debugging, but it won't show the full content)
      console.log('FormData created:', formData);

      // Send to the server with proper timeout and size settings
      const response = await axios.post('http://localhost:4000/api/upload-audio',
        formData,
        {
          headers: {
            'Content-Type': 'multipart/form-data'
          },
          timeout: 30000, // Increase timeout for larger files
          maxContentLength: 10 * 1024 * 1024 // 10MB max size
        }
      );

      setUploadedAudio(response.data.audioData);
      setLoading(false);

    } catch (err) {
      console.error('Upload failed:', err);
      setError('Failed to upload audio. ' + (err.response?.data?.message || err.message));
      setLoading(false);
    }
  };
  const [chatMsg, setChatMsg] = useState('')
