import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker, Popup, useMap } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";

// Fix Leaflet marker icon issue
import markerIcon from "leaflet/dist/images/marker-icon.png";
import markerShadow from "leaflet/dist/images/marker-shadow.png";

const customIcon = new L.Icon({
  iconUrl: markerIcon,
  shadowUrl: markerShadow,
  iconSize: [25, 41],
  iconAnchor: [12, 41],
});

// Component to update map center dynamically (only on first load)
const ChangeView = ({ center, shouldUpdate }) => {
  const map = useMap();
  if (shouldUpdate) {
    map.setView(center, map.getZoom());
  }
  return null;
};

const CurrentLocationMap = () => {
  const [initialLocation, setInitialLocation] = useState(null);
  const [markerPosition, setMarkerPosition] = useState(null);
  const [address, setAddress] = useState("Fetching location...");
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [submittedLocation, setSubmittedLocation] = useState(null);
  const [shouldUpdateMapCenter, setShouldUpdateMapCenter] = useState(true);

  // Fetch address from coordinates
  const fetchAddress = async (lat, lon) => {
    try {
      const response = await fetch(
        `https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lon}`
      );
      const data = await response.json();
      setAddress(data.display_name || "Unknown Location");
    } catch (err) {
      setAddress("Unable to fetch location name");
    }
  };

  useEffect(() => {
    if (!navigator.geolocation) {
      setError("Geolocation is not supported by your browser");
      setLoading(false);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (position) => {
        const lat = position.coords.latitude;
        const lon = position.coords.longitude;
        setInitialLocation([lat, lon]);
        setMarkerPosition([lat, lon]); // Set marker to current location
        fetchAddress(lat, lon);
        setLoading(false);
      },
      (error) => {
        setError(`Error getting location: ${error.message}`);
        setLoading(false);
      }
    );
  }, []);

  // Handle marker drag event
  const handleMarkerDrag = (event) => {
    const { lat, lng } = event.target.getLatLng();
    setMarkerPosition([lat, lng]);
    fetchAddress(lat, lng);
  };

  // Handle submit button click
  const handleSubmit = () => {
    setSubmittedLocation({
      lat: markerPosition[0],
      lon: markerPosition[1],
      address,
    });
    setShouldUpdateMapCenter(false); // Prevent map from resetting to initial location
  };

  return (
    <div className="container">
      {/* <h2>Current Location Map</h2> */}

      {loading && <p>Loading your location...</p>}
      {error && <p className="error">{error}</p>}

      {/* <div>
        <p><strong>Selected Address:</strong> {address}</p>
        <p><strong>Latitude:</strong> {markerPosition?.[0]?.toFixed(6)}</p>
        <p><strong>Longitude:</strong> {markerPosition?.[1]?.toFixed(6)}</p>
      </div> */}

      {initialLocation && (
        <div style={{ height: "400px", width: "100%" }}>
          <MapContainer
            center={initialLocation}
            zoom={13}
            scrollWheelZoom
            style={{ height: "100%", width: "100%" }}
          >
            <ChangeView center={markerPosition} shouldUpdate={shouldUpdateMapCenter} />
            <TileLayer
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            <Marker
              position={markerPosition}
              draggable={true}
              eventHandlers={{ dragend: handleMarkerDrag }}
              icon={customIcon}
            >
              <Popup>
                <b>Drag me!</b>
                <br />
                {address}
              </Popup>
            </Marker>
          </MapContainer>
        </div>
      )}

      {/* <button onClick={handleSubmit} style={{ marginTop: "10px", padding: "10px" }}>
        Submit Location
      </button> */}

    </div>
  );
};

export default CurrentLocationMap;
