"use client"

import { useState, useEffect } from "react"
import { MapContainer, TileLayer, Marker, Popup, useMap } from "react-leaflet"
import "leaflet/dist/leaflet.css"
import L from "leaflet"
import { Loader2, MapPin, Navigation, Check, AlertTriangle, Info } from "lucide-react"
import { Button } from "@/components/ui/button"
import { Card, CardContent } from "@/components/ui/card"
import { Badge } from "@/components/ui/badge"
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from "@/components/ui/tooltip"

// Fix Leaflet marker icon issue
const markerIcon = "/images/marker-icon.png" // We'll add this to public folder
const markerShadow = "/images/marker-shadow.png" // We'll add this to public folder

const customIcon = new L.Icon({
  iconUrl: markerIcon,
  shadowUrl: markerShadow,
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
})

// Component to update map center dynamically
const ChangeView = ({ center, shouldUpdate }) => {
  const map = useMap()
  if (shouldUpdate) {
    map.setView(center, map.getZoom())
  }
  return null
}

const CurrentLocationMap = () => {
  const [initialLocation, setInitialLocation] = useState(null)
  const [markerPosition, setMarkerPosition] = useState(null)
  const [address, setAddress] = useState("Fetching location...")
  const [error, setError] = useState(null)
  const [loading, setLoading] = useState(true)
  const [submittedLocation, setSubmittedLocation] = useState(null)
  const [shouldUpdateMapCenter, setShouldUpdateMapCenter] = useState(true)
  const [mapStyle, setMapStyle] = useState("streets") // streets, satellite, dark

  // Fetch address from coordinates
  const fetchAddress = async (lat, lon) => {
    try {
      const response = await fetch(`https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lon}`)
      const data = await response.json()
      setAddress(data.display_name || "Unknown Location")
    } catch (err) {
      setAddress("Unable to fetch location name")
    }
  }

  useEffect(() => {
    if (!navigator.geolocation) {
      setError("Geolocation is not supported by your browser")
      setLoading(false)
      return
    }

    navigator.geolocation.getCurrentPosition(
      (position) => {
        const lat = position.coords.latitude
        const lon = position.coords.longitude
        setInitialLocation([lat, lon])
        setMarkerPosition([lat, lon]) // Set marker to current location
        fetchAddress(lat, lon)
        setLoading(false)
      },
      (error) => {
        setError(`Error getting location: ${error.message}`)
        // Fallback to New York City coordinates
        setInitialLocation([40.7128, -74.006])
        setMarkerPosition([40.7128, -74.006])
        setLoading(false)
      },
    )
  }, [])

  // Handle marker drag event
  const handleMarkerDrag = (event) => {
    const { lat, lng } = event.target.getLatLng()
    setMarkerPosition([lat, lng])
    fetchAddress(lat, lng)
  }

  // Handle submit button click
  const handleSubmit = () => {
    setSubmittedLocation({
      lat: markerPosition[0],
      lon: markerPosition[1],
      address,
    })
    setShouldUpdateMapCenter(false) // Prevent map from resetting to initial location
  }

  // Get tile layer URL based on selected style
  const getTileLayer = () => {
    switch (mapStyle) {
      case "satellite":
        return "https://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}"
      case "dark":
        return "https://tiles.stadiamaps.com/tiles/alidade_smooth_dark/{z}/{x}/{y}{r}.png"
      default: // streets
        return "https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
    }
  }

  // Get attribution based on selected style
  const getAttribution = () => {
    switch (mapStyle) {
      case "satellite":
        return '&copy; <a href="https://www.arcgis.com/">Esri</a>'
      case "dark":
        return '&copy; <a href="https://stadiamaps.com/">Stadia Maps</a>'
      default: // streets
        return '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
    }
  }

  return (
    <div className="location-map-container">
      <div className="map-header">
        <h2 className="map-title">
          <MapPin className="map-icon" /> Location Selector
        </h2>
        <div className="map-style-selector">
          <Button
            size="sm"
            variant={mapStyle === "streets" ? "default" : "outline"}
            onClick={() => setMapStyle("streets")}
            className="map-style-btn"
          >
            Streets
          </Button>
          <Button
            size="sm"
            variant={mapStyle === "satellite" ? "default" : "outline"}
            onClick={() => setMapStyle("satellite")}
            className="map-style-btn"
          >
            Satellite
          </Button>
          <Button
            size="sm"
            variant={mapStyle === "dark" ? "default" : "outline"}
            onClick={() => setMapStyle("dark")}
            className="map-style-btn"
          >
            Dark
          </Button>
        </div>
      </div>

      {loading ? (
        <div className="map-loading">
          <Loader2 className="animate-spin" size={36} />
          <p>Locating you on the map...</p>
        </div>
      ) : error ? (
        <div className="map-error">
          <AlertTriangle size={36} />
          <p>{error}</p>
        </div>
      ) : (
        <>
          <Card className="location-info-card">
            <CardContent>
              <div className="location-info">
                <div className="location-address">
                  <Badge variant="outline" className="location-badge">
                    Selected Location
                  </Badge>
                  <p className="address-text">{address}</p>
                </div>
                <div className="location-coordinates">
                  <Badge variant="outline" className="location-badge">
                    Coordinates
                  </Badge>
                  <div className="coordinates-container">
                    <span>
                      Lat: {markerPosition?.[0]?.toFixed(6)}째 | Long: {markerPosition?.[1]?.toFixed(6)}째
                    </span>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>

          {initialLocation && (
            <div className="map-container">
              <MapContainer center={initialLocation} zoom={14} scrollWheelZoom className="leaflet-container">
                <ChangeView center={markerPosition} shouldUpdate={shouldUpdateMapCenter} />
                <TileLayer attribution={getAttribution()} url={getTileLayer()} />
                <Marker
                  position={markerPosition}
                  draggable={true}
                  eventHandlers={{ dragend: handleMarkerDrag }}
                  icon={customIcon}
                >
                  <Popup className="custom-popup">
                    <div className="popup-content">
                      <h3>
                        <Navigation className="popup-icon" /> Drag to adjust location
                      </h3>
                      <p>{address}</p>
                    </div>
                  </Popup>
                </Marker>
              </MapContainer>

              <div className="map-instructions">
                <TooltipProvider>
                  <Tooltip>
                    <TooltipTrigger asChild>
                      <div className="instruction-icon">
                        <Info size={20} />
                      </div>
                    </TooltipTrigger>
                    <TooltipContent>
                      <p>Drag the marker to adjust the exact location</p>
                    </TooltipContent>
                  </Tooltip>
                </TooltipProvider>
                <p>Drag the marker to adjust the exact location</p>
              </div>
            </div>
          )}

          <Button onClick={handleSubmit} className="submit-location-btn" size="lg">
            <Check className="mr-2" size={18} />
            Confirm This Location
          </Button>

          {submittedLocation && (
            <div className="submitted-location">
              <Badge variant="default" className="submitted-badge">
                Location Confirmed
              </Badge>
              <p>
                You've confirmed the location at {submittedLocation.lat.toFixed(6)}째,
                {submittedLocation.lon.toFixed(6)}째
              </p>
            </div>
          )}
        </>
      )}
    </div>
  )
}

export default CurrentLocationMap
