  const [isRecording, setIsRecording] = useState(false);
  const [audioUrl, setAudioUrl] = useState("");
  const [message, setMessage] = useState("");
  const [isUploading, setIsUploading] = useState(false);
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);

  // 🎙️ Start Recording
  const startRecording = async () => {
    try {
      audioChunksRef.current = [];
      setAudioUrl("");
      setMessage("");

      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaRecorderRef.current = new MediaRecorder(stream);

      mediaRecorderRef.current.ondataavailable = (event) => {
        if (event.data.size > 0) {
          audioChunksRef.current.push(event.data);
        }
      };

      mediaRecorderRef.current.onstop = () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: "audio/mpeg" });
        const url = URL.createObjectURL(audioBlob);
        setAudioUrl(url);
      };

      mediaRecorderRef.current.start();
      setIsRecording(true);
    } catch (error) {
      console.error("Error accessing microphone:", error);
      setMessage("Error accessing microphone. Ensure permission is granted.");
    }
  };

  // 🛑 Stop Recording
  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      mediaRecorderRef.current.stream.getTracks().forEach((track) => track.stop());
      setIsRecording(false);
      setMessage("Recording stopped. Click Upload to send the file.");
    }
  };

  // 📤 Upload Audio
// Add more robust error handling and debug logging
const uploadAudio = async () => {
  if (!audioUrl) {
    setMessage("No recording to upload. Record audio first.");
    return;
  }

  setIsUploading(true);
  setMessage("Uploading audio...");

  try {
    const response = await fetch(audioUrl);
    const audioBlob = await response.blob();

    if (audioBlob.size === 0) {
      setMessage("Audio file is empty. Try recording again.");
      setIsUploading(false);
      return;
    }

    // Log more details about the blob
    console.log("Audio blob details:", {
      size: audioBlob.size,
      type: audioBlob.type
    });

    const audioFile = new File([audioBlob], "recorded_audio.mp3", {
      type: "audio/mpeg",
      lastModified: new Date().getTime(),
    });

    const formData = new FormData();
    formData.append("audioFile", audioFile);

    // Use a longer timeout
    const uploadResponse = await axios.post(
      "http://localhost:4000/audio/upload", 
      formData, 
      {
        headers: { "Content-Type": "multipart/form-data" },
        timeout: 30000, // 30 seconds
      }
    );

    setMessage(`Audio file uploaded successfully: ${uploadResponse.data.file.filename}`);
  } catch (error) {
    console.error("Upload error details:", error);

    if (error.response) {
      // The server responded with a status code outside the 2xx range
      setMessage(`Server error: ${error.response.data.message || error.response.statusText}`);
    } else if (error.request) {
      // The request was made but no response was received
      setMessage("No response from server. Check if server is running.");
    } else {
      // Something happened in setting up the request
      setMessage(`Error preparing upload: ${error.message}`);
    }
  } finally {
    setIsUploading(false);
  }
};


