import React, { useState } from 'react';
import { ArrowLeft } from 'lucide-react';
import CurrentLocationMap from '../../components/Map/Map'
import axios from 'axios';

// Mock components
const Button = ({ size, className, children }) => {
  const sizeClasses = {
    lg: 'py-3 px-6 text-lg'
  };

  return (
    <button
      className={`bg-black text-white rounded transition-colors hover:bg-gray-800 ${sizeClasses[size] || 'py-2 px-4'} ${className || ''}`}
    >
      {children}
    </button>
  );
};

const Card = ({ className, children }) => (
  <div className={`bg-white rounded-lg overflow-hidden shadow-md ${className || ''}`}>
    {children}
  </div>
);

const CardHeader = ({ children }) => (
  <div className="p-6 border-b border-gray-200">
    {children}
  </div>
);

const CardTitle = ({ children }) => (
  <h3 className="text-xl font-bold text-gray-900 mb-2">
    {children}
  </h3>
);

const CardDescription = ({ children }) => (
  <p className="text-gray-600">
    {children}
  </p>
);

const CardContent = ({ children }) => (
  <div className="p-6">
    {children}
  </div>
);

const Tabs = ({ defaultValue, children }) => {
  const [activeTab, setActiveTab] = React.useState(defaultValue);

  // Clone children and pass activeTab state
  const childrenWithProps = React.Children.map(children, child => {
    if (React.isValidElement(child)) {
      return React.cloneElement(child, { activeTab, setActiveTab });
    }
    return child;
  });

  return <div>{childrenWithProps}</div>;
};

const TabsList = ({ className, children, activeTab, setActiveTab }) => {
  // Clone TabsTrigger children and pass state
  const childrenWithProps = React.Children.map(children, child => {
    if (React.isValidElement(child)) {
      return React.cloneElement(child, { activeTab, setActiveTab });
    }
    return child;
  });

  return (
    <div className={`flex rounded-md bg-gray-100 p-1 ${className || ''}`}>
      {childrenWithProps}
    </div>
  );
};

const TabsTrigger = ({ value, children, activeTab, setActiveTab }) => (
  <button
    className={`flex-1 text-center py-2 px-4 rounded ${activeTab === value
      ? 'bg-white text-black-600 shadow-sm'
      : 'text-gray-600 hover:text-gray-900'
      }`}
    onClick={() => setActiveTab(value)}
  >
    {children}
  </button>
);

const TabsContent = ({ value, children, activeTab }) => {
  if (value !== activeTab) return null;
  return <div>{children}</div>;
};

// import React, { useState } from 'react';
// import axios from 'axios';

// const UploadAudio = () => {

//   return (
//     <div>
//       <h2>Upload an Audio File</h2>
//       <form onSubmit={handleSubmit}>
//         <input type="file" accept="audio/*" onChange={handleFileChange} />
//         <button type="submit">Upload</button>
//       </form>
//       <p>{message}</p>
//     </div>
//   );
// };

// export default UploadAudio;

// Mock form components
const ReportForm = () => {
  const [formData, setFormData] = useState({
    crimetype: "",
    description: "",
    crimeDate: "",
    crimeTime: "",
    location: {
      coordinates: [], // [longitude, latitude]
      address: ""
    },
    crimeimageURL: ""
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [selectedFile, setSelectedFile] = useState(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleLocationUpdate = (coordinates, address) => {
    setFormData({
      ...formData,
      location: {
        coordinates: [coordinates[1], coordinates[0]], // MongoDB uses [longitude, latitude]
        address: address
      }
    });
  };

  const handleFileChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      setSelectedFile(e.target.files[0]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    // Create form data for file upload
    const submitData = new FormData();

    // Add the JSON data
    const jsonData = {
      crimetype: formData.crimetype,
      description: formData.description,
      crimeDate: formData.crimeDate,
      crimeTime: formData.crimeTime,
      location: {
        type: "Point",
        coordinates: formData.location.coordinates
      }
    };

    submitData.append("data", JSON.stringify(jsonData));

    // Add the file if selected
    if (selectedFile) {
      submitData.append("crimeimage", selectedFile);
    }

    try {
      const response = await fetch("http://localhost:4000/report/add-new", {
        method: "POST",
        body: submitData, // No Content-Type header as FormData sets it automatically
      });

      const data = await response.json();

      if (response.ok) {
        setMessage("Report submitted successfully!");
        // Reset form
        setFormData({
          crimetype: "",
          description: "",
          crimeDate: "",
          crimeTime: "",
          location: {
            coordinates: [],
            address: ""
          },
          crimeimageURL: ""
        });
        setSelectedFile(null);
      } else {
        setMessage(`Error: ${data.message}`);
      }
    } catch (error) {
      setMessage("Something went wrong. Please try again.");
      console.error(error);
    }

    setLoading(false);
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <div className="bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-xl font-bold mb-6">Report an Incident</h2>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Incident Type</label>
          <select
            name="crimetype"
            value={formData.crimetype}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            required
          >
            <option value="">Select incident type</option>
            <option value="Theft">Theft</option>
            <option value="Assault">Assault</option>
            <option value="Vandalism">Vandalism</option>
            <option value="Suspicious Activity">Suspicious Activity</option>
            <option value="Other">Other</option>
          </select>
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Location</label>
          <div className="border rounded p-4">
            <CurrentLocationMap 
              formData={formData}
              onLocationSelect={handleLocationUpdate}
            />
            <p className="mt-2 text-sm text-gray-600">
              Drag the marker to pinpoint the exact location of the incident
            </p>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
          <div>
            <label className="block text-gray-700 mb-2">Date</label>
            <input
              type="date"
              name="crimeDate"
              value={formData.crimeDate}
              onChange={handleChange}
              className="w-full p-2 border rounded"
              required
            />
          </div>

          <div>
            <label className="block text-gray-700 mb-2">Time</label>
            <input
              type="time"
              name="crimeTime"
              value={formData.crimeTime}
              onChange={handleChange}
              className="w-full p-2 border rounded"
              required
            />
          </div>
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Description</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            className="w-full p-2 border rounded h-32"
            placeholder="Provide details about the incident..."
            required
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Upload Evidence (optional)</label>
          <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center">
            <p className="text-gray-500 mb-2">
              {selectedFile ? selectedFile.name : "Drag & drop files here or click to browse"}
            </p>
            <input
              type="file"
              id="fileInput"
              onChange={handleFileChange}
              className="hidden"
              accept="image/*"
            />
            <Button 
              type="button" 
              variant="outline" 
              size="sm"
              onClick={() => document.getElementById('fileInput').click()}
            >
              Choose Files
            </Button>
          </div>
        </div>

        {message && (
          <div className={`p-3 rounded mb-4 ${message.includes('Error') ? 'bg-red-100 text-red-800' : 'bg-green-100 text-green-800'}`}>
            {message}
          </div>
        )}

        <Button 
          type="submit" 
          className="w-full"
          disabled={loading}
        >
          {loading ? "Submitting..." : "Submit Report"}
        </Button>
      </div>
    </form>
  );
};

// Mock chatbot assistant
const ChatbotAssistant = () => (
  <div className="rounded-lg bg-gray-50 p-4 h-96 flex flex-col">
    <div className="flex-1 overflow-y-auto mb-4 space-y-4">
      <div className="bg-gray-300 rounded p-3 max-w-[80%]">
        <p className="text-gray-800">Hello! I'm your SafetyWatch assistant. How can I help you with your report today?</p>
      </div>
    </div>

    <div className="flex gap-2">
      <input
        type="text"
        className="flex-1 p-2 rounded bg-white border border-gray-300 text-gray-900"
        placeholder="Type your message..."
      />
      <Button>Send</Button>
    </div>
  </div>
);

export default function ReportPage() {
  const [audioFile, setAudioFile] = useState(null);
  const [message, setMessage] = useState('');

  // Handle file selection
  const handleFileChange = (event) => {
    setAudioFile(event.target.files[0]);
  };

  // Handle form submission
  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!audioFile) {
      setMessage('Please select an audio file first.');
      return;
    }

    const formData = new FormData();
    formData.append('audioFile', audioFile);

    try {
      // Send the file to the backend server
      const response = await axios.post('http://localhost:3000/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setMessage(`Audio file uploaded successfully: ${response.data.filename}`);
    } catch (error) {
      setMessage('Error uploading the audio file.');
    }
  };


  return (
    <div className="min-h-screen bg-gray-50 text-gray-900">
      <div className="container mx-auto px-4 py-8">
        <a href="/" className="flex items-center gap-2 mb-6 text-gray-500 hover:text-gray-900">
          <ArrowLeft className="h-4 w-4" />
          <span>Back to Home</span>
        </a>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          <div className="md:col-span-2">
            <Card>
              <CardHeader>
                <CardTitle>Report an Incident</CardTitle>
                <CardDescription>
                  Fill out the form below to report a crime or suspicious activity. All reports are reviewed by law
                  enforcement before being published.
                </CardDescription>
              </CardHeader>
              <CardContent>
                <Tabs defaultValue="form">
                  <TabsList className="grid grid-cols-2 mb-6 w-full">
                    <TabsTrigger value="form">Form</TabsTrigger>
                    <TabsTrigger value="voice">Voice Report</TabsTrigger>
                  </TabsList>
                  <TabsContent value="form">
                    {/* form */}
                    <ReportForm />
                  </TabsContent>
                  <TabsContent value="voice">
                    <div className="flex flex-col items-center justify-center py-12 space-y-4">
                      <div className="w-16 h-16 rounded-full bg-gray-300 flex items-center justify-center">
                        <svg
                          xmlns="http://www.w3.org/2000/svg"
                          viewBox="0 0 24 24"
                          fill="none"
                          stroke="currentColor"
                          strokeWidth="2"
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          className="h-8 w-8 text-black-600"
                        >
                          <path d="M12 2a3 3 0 0 0-3 3v7a3 3 0 0 0 6 0V5a3 3 0 0 0-3-3Z" />
                          <path d="M19 10v2a7 7 0 0 1-14 0v-2" />
                          <line x1="12" x2="12" y1="19" y2="22" />
                        </svg>
                      </div>
                      <h3 className="text-xl font-medium text-gray-900">Voice Reporting</h3>
                      <p className="text-center text-gray-600 max-w-md">
                        Click the button below to start recording your report. Speak clearly and provide as much detail as
                        possible.
                      </p>

                      {/* <div>
                        <h2>Upload an Audio File</h2>
                        <form onSubmit={handleSubmit}>
                          <input type="file" accept="audio/*" onChange={handleFileChange} />
                          <button type="submit">Upload</button>
                        </form>
                        <p>{message}</p>
                      </div> */}
                      <Button size="lg" className="mt-4">
                        Start Recording
                      </Button>
                    </div>
                  </TabsContent>
                </Tabs>
              </CardContent>
            </Card>
          </div>

          <div>
            <Card className="sticky top-8">
              <CardHeader>
                <CardTitle>Need Help?</CardTitle>
                <CardDescription>
                  Our AI assistant can guide you through the reporting process and answer questions about crime reporting.
                </CardDescription>
              </CardHeader>
              <CardContent>
                <ChatbotAssistant />
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    </div>
  );
}
